jQuery(function() {
	initSlickCarousel();
	initAccordion();
	initCustomForms();
	initFancybox();
	initPopups();
	initMobileNav();
	initMasonry();
	initCustomHover();
	initDesignPanel();
	initProductGallery();
});

function initDesignPanel() {
	var defaultText = 'Wall Panel';
	var activeClass = 'active';

	jQuery('.panels').each(function(){
		var holder = jQuery(this);
		var list = holder.find('ul');
		var links = list.find('a');
		var visualBox = holder.find('.visual-box');
		var heading = visualBox.find('.title');
		var image = visualBox.find('.img-holder');
		var text;
		var bg;

		function setPanel(link) {
			text = link.text() + ' ' + defaultText;
			bg = 'url(' + link.data('images') + ')';

			links.removeClass(activeClass);
			link.addClass(activeClass);
			heading.text(text);
			image.css({
				'background-image': bg
			});
		}

		setPanel(links.eq(0));
		links.on('mouseover', function(e){
			setPanel(jQuery(e.target));
		});
	});
}

function initProductGallery() {
	jQuery('.features').productGallery({
		url: '#' // add here correct url to send project id
	});
	jQuery('.gallery').productGallery({
		url: '#' // add here correct url to send project id
	});
}

// slick init
function initSlickCarousel() {
	jQuery('.testimonials-slider').slick({
		slidesToScroll: 1,
		rows: 0,
		arrows: false,
		dots: true,
		dotsClass: 'slick-dots',
		fade: true,
		adaptiveHeight: true
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.faqs').slideAccordion({
		opener: '.opener',
		slider: '.slide',
		animSpeed: 300
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		wrapNativeOnMobile: false,
	});
	jcf.replaceAll();
}

// lightbox init
function initFancybox() {
	jQuery('a.lightbox, [data-fancybox]').fancybox({
		parentEl: 'body',
		margin: [50, 0]
	});
}

// popups init
function initPopups() {
	jQuery('.checkout-form').contentPopup({
		mode: 'click',
		popup: '.popup-box',
		btnOpen: '.popup-open',
		btnClose: '.popup-close'
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		hideOnClickOutside: true,
		menuDrop: '#nav'
	});
}

// masonry init
function initMasonry() {
	jQuery('.masonry').masonry({
		itemSelector: '.grid-item',
		columnWidth: 1,
		resize: true,
		isOriginLeft: true
	});
}

// add classes on hover/touch
function initCustomHover() {
	jQuery('#nav > ul > li').touchHover({});
}

